SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_movto_trans_financ ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*'
SQP - Sequência da perda
DTC - Data da Conciliação
'*/
nr_seq_perda_w		bigint;
nr_seq_movto_orig_w	bigint;
ds_retorno_w		varchar(255);
dt_conciliacao_w	timestamp;


BEGIN
if (ie_opcao_p = 'SQP') then
	begin
	select	max(nr_seq_perda),
		max(nr_seq_movto_orig)
	into STRICT	nr_seq_perda_w,
		nr_seq_movto_orig_w
	from	movto_trans_financ
	where	nr_sequencia = nr_sequencia_p;

	if (coalesce(nr_seq_perda_w::text, '') = '') and (nr_seq_movto_orig_w IS NOT NULL AND nr_seq_movto_orig_w::text <> '') then
		select	max(nr_seq_perda)
		into STRICT	nr_seq_perda_w
		from	movto_trans_financ
		where	nr_sequencia = nr_seq_movto_orig_w;
	end if;

	ds_retorno_w	:= nr_seq_perda_w;
	end;
elsif (ie_opcao_p = 'DTC') then
	begin

	select	max(c.dt_conciliacao)
	into STRICT	dt_conciliacao_w
	from	movto_Trans_financ a,
		concil_banc_movto b,
		conciliacao_bancaria c
	where	b.nr_sequencia          = a.nr_seq_concil
	and	b.nr_seq_conciliacao	= c.nr_sequencia
	and	a.nr_sequencia		= nr_sequencia_p;

	if (dt_conciliacao_w IS NOT NULL AND dt_conciliacao_w::text <> '') then
		ds_retorno_w	:= to_char(dt_conciliacao_w,'dd/mm/yyyy');
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
