CREATE OR REPLACE FUNCTION obter_dados_mucosite ( nr_sequencia_p bigint, nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* 
Protocolo prescrito na SAE com ades√£o	'PA' 
*/
 
			 
qt_prescrito_adesao_w	bigint;
ds_retorno_w		varchar(255);
	

BEGIN 
 
 
if (ie_opcao_p = 'PA') then 
 
	select 	count(*) 
	into STRICT	qt_prescrito_adesao_w 
	from	pe_prescricao a, 
		gqa_pendencia_pac b, 
		gqa_pendencia_regra c, 
		gqa_pend_pac_acao e 
	where	e.nr_seq_superior = nr_sequencia_p 
	and	a.nr_seq_pend_pac_acao = e.nr_sequencia 
	and	e.nr_seq_pend_pac = b.nr_sequencia 
	and	b.nr_seq_pend_regra = c.nr_sequencia 
	and	b.ie_escala = 100;
 
end if;
 
ds_retorno_w := qt_prescrito_adesao_w;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
