SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_nascimento_atend (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w			varchar(255);
ie_tipo_nascimento_w		nascimento.ie_tipo_nascimento%type;
/*
'TN' - Tipo de nascimento
*/
BEGIN

begin
select	a.ie_tipo_nascimento
into STRICT	ie_tipo_nascimento_w
from	nascimento a
where	a.DT_NASCIMENTO = 	(SELECT max(x.dt_nascimento)
				from	nascimento x
				where	x.nr_atendimento = a.nr_atendimento);
exception
when others then
	ie_tipo_nascimento_w := null;
end;


if (ie_opcao_p = 'TN') then
	ds_retorno_w := ie_tipo_nascimento_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
