SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_natureza_operacao ( cd_natureza_operacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p:
	CFOP - Codigo Fiscal
	ISS - Exigibilidade ISS
	DISS - Observação da situação tributária
	D - Descrição
*/
cd_cfop_w			natureza_operacao.cd_cfop%type;
cd_cfop_inverso_w		natureza_operacao.cd_cfop_inverso%type;
nr_seq_trib_prestador_w		natureza_operacao.nr_seq_trib_prestador%type;
ds_natureza_operacao_w		natureza_operacao.ds_natureza_operacao%type;
ds_retorno_w			varchar(255) := '';


BEGIN

select	max(cd_cfop),
	max(cd_cfop_inverso),
	max(nr_seq_trib_prestador),
	substr(max(ds_natureza_operacao),1,255)
into STRICT	cd_cfop_w,
	cd_cfop_inverso_w,
	nr_seq_trib_prestador_w,
	ds_natureza_operacao_w
from	natureza_operacao
where	cd_natureza_operacao = cd_natureza_operacao_p;

if (ie_opcao_p = 'CF') then

	ds_retorno_w	:= cd_cfop_w;

elsif (ie_opcao_p = 'CFI') then

	ds_retorno_w	:= cd_cfop_inverso_w;

elsif (ie_opcao_p = 'ISS') then

	select obter_dados_situacao_trib(coalesce(nr_seq_trib_prestador_w,0),'CD')
	into STRICT ds_retorno_w
	;

elsif (ie_opcao_p = 'DISS') then

	select obter_dados_situacao_trib(coalesce(nr_seq_trib_prestador_w,0),'OBS')
	into STRICT ds_retorno_w
	;

elsif (ie_opcao_p = 'D') then

	ds_retorno_w := ds_natureza_operacao_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
