SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_negociacao_audit (nr_seq_lote_audit_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
1 - Usuários responsáveis,
2 - Data prevista de contato
3 - Data último contato
4 - Dias úteis do último contato
5 - Data do último histórico
*/
nm_usuario_w		varchar(255);
ds_retorno_w		varchar(4000);
qt_dias_contato_w	bigint;
dt_ultimo_contato_w	timestamp;
dt_proximo_dia_w	timestamp;
cd_estabelecimento_w	bigint;

c01 CURSOR FOR
SELECT	distinct
	obter_nome_usuario(nm_usuario_resp)
from	negociacao_lote_audit_resp
where	nr_seq_lote_audit	= nr_seq_lote_audit_p
order by 1;


BEGIN

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	lote_auditoria
where	nr_sequencia	= nr_seq_lote_audit_p;

if (coalesce(nr_seq_lote_audit_p,0) > 0) then

	if (ie_opcao_p = '1') then

		open C01;
		loop
		fetch C01 into
			nm_usuario_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			ds_retorno_w	:= ds_retorno_w || nm_usuario_w || ', ';

			end;
		end loop;
		close C01;

		if (length(ds_retorno_w) > 2) then
			ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w) - 2);
		end if;

	elsif (ie_opcao_p = '2') then

		select	to_char(max(dt_previsao_contato),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_retorno_w
		from	negociacao_lote_audit_resp
		where	nr_seq_lote_audit	= nr_seq_lote_audit_p;

	elsif (ie_opcao_p = '3') then

		select	to_char(max(dt_ultimo_contato),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_retorno_w
		from	negociacao_lote_audit_resp
		where	nr_seq_lote_audit	= nr_seq_lote_audit_p;

	elsif (ie_opcao_p = '4') then

		select	max(dt_ultimo_contato)
		into STRICT	dt_ultimo_contato_w
		from	negociacao_lote_audit_resp
		where	nr_seq_lote_audit	= nr_seq_lote_audit_p;

		if (dt_ultimo_contato_w IS NOT NULL AND dt_ultimo_contato_w::text <> '') then

			dt_proximo_dia_w	:= dt_ultimo_contato_w + 1;
			qt_dias_contato_w	:= 0;

			while	dt_proximo_dia_w < clock_timestamp() loop
				begin
				if (obter_se_dia_util(dt_proximo_dia_w,cd_estabelecimento_w) = 'S') then
					qt_dias_contato_w	:= qt_dias_contato_w + 1;
				end if;
				dt_proximo_dia_w	:= dt_proximo_dia_w + 1;
				end;
			end loop;

			ds_retorno_w	:= to_char(qt_dias_contato_w);
		else
			ds_retorno_w	:= null;
		end if;

	elsif (ie_opcao_p = '5') then

		select	to_char(max(a.dt_historico),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_retorno_w
		from	negociacao_lote_audit_hist a
		where	a.nr_seq_lote_audit	= nr_seq_lote_audit_p;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
