SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_negociacao_cr ( nr_seq_negociacao_p bigint, ie_dado_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_dado_p
'QP' - Quantidade de parcelas
'PP'  - Parcelas permitidas
'FP' - Forma de pagamento principal
*/
ds_retorno_w		varchar(255);
nr_seq_regra_parcela_w	bigint;
qt_registro_w		bigint;


BEGIN

if (nr_seq_negociacao_p IS NOT NULL AND nr_seq_negociacao_p::text <> '') then
	if (ie_dado_p = 'QP') then
		select	count(*)
		into STRICT	ds_retorno_w
		from	negociacao_cr_parcela a
		where	a.nr_seq_negociacao	= nr_seq_negociacao_p
		and	(a.vl_parcela IS NOT NULL AND a.vl_parcela::text <> '');
	elsif (ie_dado_p = 'PP') then
		select	a.nr_seq_regra_parcela
		into STRICT	nr_seq_regra_parcela_w
		from	negociacao_cr a
		where	a.nr_sequencia	= nr_seq_negociacao_p;

		if (nr_seq_regra_parcela_w IS NOT NULL AND nr_seq_regra_parcela_w::text <> '') then
			select	max(a.nr_parcelas)
			into STRICT	ds_retorno_w
			from	regra_parcela_neg_cr a
			where	a.nr_sequencia	= nr_seq_regra_parcela_w;
		end if;
	elsif (ie_dado_p = 'FP') then
		select	max(qt_registro)
		into STRICT	qt_registro_w
		from (SELECT	count(*) qt_registro,
				ie_forma_pagto
			from	negociacao_cr_parcela
			where	nr_seq_negociacao = nr_seq_negociacao_p
			and	(ie_forma_pagto IS NOT NULL AND ie_forma_pagto::text <> '')
			group by ie_forma_pagto) alias4;

		select	max(a.ie_forma_pagto)
		into STRICT	ds_retorno_w
		from	negociacao_cr_parcela a
		where	a.nr_seq_negociacao = nr_seq_negociacao_p
		and	(a.ie_forma_pagto IS NOT NULL AND a.ie_forma_pagto::text <> '')
		and (SELECT	count(*)
			from	negociacao_cr_parcela x
			where	x.nr_seq_negociacao 	= a.nr_seq_negociacao
			and	x.ie_forma_pagto	= a.ie_forma_pagto) = qt_registro_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
