SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_nota_credito (nr_seq_nota_credito_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
'CDPF'		Código da pessoa física		cd_pessoa_fisica
'PF'		Nome da pessoa física		nm_pessoa_fisica
'CDPJ'		Código da pessoa jurídica	cd_cgc
'PJ'		Nome da pessoa jurídica		nm_pessoa_juridica
'DT'		Data da nota de crédito		dt_nota_credito
'DV'		Data de vencimento		dt_vencimento
'VL'		Valor da nota de crédito	vl_nota_credito
'VS'		Saldo da nota de crédito	vl_saldo
'TP'		Título a pagar			nr_titulo
'SM'		Sequência do motivo		nr_seq_motivo
'DM'		Descrição do motivo		ds_motivo
'LA'		Número do lote auditoria	nr_seq_lote_audit
'LH'		Número do lote de histórico	nr_seq_lote_hist
'LC'		Lote de cobrança escritural
'SI'		Situação			ie_situacao
*/
ds_retorno_w		varchar(4000);
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);
dt_nota_credito_w	timestamp;
dt_vencimento_w		timestamp;
vl_nota_credito_w	double precision;
vl_saldo_w		double precision;
nr_titulo_w		bigint;
ds_motivo_w		varchar(255);
nr_seq_lote_audit_w	bigint;
nr_seq_lote_hist_w	bigint;
nr_seq_lote_guia_w	bigint;
nr_seq_motivo_w		bigint;
ie_situacao_w		varchar(10);


BEGIN

select	a.cd_pessoa_fisica,
	a.cd_cgc,
	a.dt_nota_credito,
	a.dt_vencimento,
	a.vl_nota_credito,
	a.vl_saldo,
	a.nr_seq_motivo,
	a.nr_seq_lote_audit_hist,
	a.nr_seq_lote_hist_guia,
	a.ie_situacao
into STRICT	cd_pessoa_fisica_w,
	cd_cgc_w,
	dt_nota_credito_w,
	dt_vencimento_w,
	vl_nota_credito_w,
	vl_saldo_w,
	nr_seq_motivo_w,
	nr_seq_lote_hist_w,
	nr_seq_lote_guia_w,
	ie_situacao_w
from	nota_credito a
where	a.nr_sequencia	= nr_seq_nota_credito_p;

if (ie_opcao_p	= 'CDPF') then
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_opcao_p	= 'PF') then
	ds_retorno_w	:= substr(obter_nome_pf_pj(cd_pessoa_fisica_w,null),1,255);
elsif (ie_opcao_p	= 'CDPJ') then
	ds_retorno_w	:= cd_cgc_w;
elsif (ie_opcao_p	= 'PJ') then
	ds_retorno_w	:= substr(obter_nome_pf_pj(null,cd_cgc_w),1,255);
elsif (ie_opcao_p	= 'DT') then
	ds_retorno_w	:= dt_nota_credito_w;
elsif (ie_opcao_p	= 'DV') then
	ds_retorno_w	:= dt_vencimento_w;
elsif (ie_opcao_p	= 'VL') then
	ds_retorno_w	:= vl_nota_credito_w;
elsif (ie_opcao_p	= 'VS') then
	ds_retorno_w	:= vl_saldo_w;
elsif (ie_opcao_p	= 'SI') then
	ds_retorno_w	:= ie_situacao_w;
elsif (ie_opcao_p	= 'TP') then
	select	max(a.nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_pagar a
	where	a.nr_seq_nota_credito	= nr_seq_nota_credito_p;
	
	if (coalesce(nr_titulo_w::text, '') = '') then
		select	max(c.nr_titulo)
		into STRICT	nr_titulo_w
		from	titulo_pagar c,
			bordero_nc b,
			bordero_nc_nota_credito a
		where	a.nr_seq_nota_credito	= nr_seq_nota_credito_p
		and	a.nr_seq_bordero	= b.nr_sequencia
		and	b.nr_sequencia		= c.nr_seq_bordero_nc;
	end if;
	ds_retorno_w	:= nr_titulo_w;
elsif (ie_opcao_p	= 'SM') then
	ds_retorno_w	:= nr_seq_motivo_w;
elsif (ie_opcao_p	= 'DM') then
	select	a.ds_motivo
	into STRICT	ds_motivo_w
	from	motivo_nota_credito a
	where	a.nr_sequencia	= nr_seq_motivo_w;

	ds_retorno_w	:= ds_motivo_w;
elsif (ie_opcao_p	= 'LA') then
	select	a.nr_seq_lote_audit
	into STRICT	nr_seq_lote_audit_w
	from	lote_audit_hist a
	where	a.nr_sequencia	= nr_seq_lote_hist_w;

	if (coalesce(nr_seq_lote_audit_w::text, '') = '') then
		select	b.nr_seq_lote_audit
		into STRICT	nr_seq_lote_audit_w
		from	lote_audit_hist b,
			lote_audit_hist_guia a
		where	a.nr_sequencia		= nr_seq_lote_guia_w
		and	a.nr_seq_lote_hist	= b.nr_sequencia;
	end if;
	ds_retorno_w	:= nr_seq_lote_audit_w;
elsif (ie_opcao_p	= 'LH') then
	if (coalesce(nr_seq_lote_hist_w::text, '') = '') then
		select	a.nr_seq_lote_hist
		into STRICT	nr_seq_lote_hist_w
		from	lote_audit_hist_guia a
		where	a.nr_sequencia	= nr_seq_lote_guia_w;
	end if;	
	ds_retorno_w	:= nr_seq_lote_hist_w;
elsif (ie_opcao_p	= 'LC') then
	select	b.nr_seq_cobranca
	into STRICT	ds_retorno_w
	from	titulo_receber_cobr	b,
		nota_credito		a
	where	b.nr_sequencia	= a.nr_seq_tit_rec_cobr
	and	a.nr_sequencia	= nr_seq_nota_credito_p;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_nota_credito (nr_seq_nota_credito_p bigint, ie_opcao_p text) FROM PUBLIC;

