CREATE OR REPLACE FUNCTION obter_dados_nota_fiscal_dt ( nr_seq_nota_p bigint, ie_tipo_dado_p text) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w			 timestamp;
dt_emissao_w			 timestamp;
dt_entrada_saida_w		 timestamp;
dt_atualizacao_estoque_w timestamp;
/*
9  - Data Emissão NF
11 - Data Emissão NF Ano
13 - Data entrada/saída
21 - Dt vencimento (1º vecto da nota)
27 - Data de atualização do estoque
*/
BEGIN

if (coalesce(nr_seq_nota_p,0) > 0) then
	select max(dt_emissao),
		   max(dt_entrada_saida),
		   max(dt_atualizacao_estoque)
	into STRICT
		dt_emissao_w,
		dt_entrada_saida_w,
		dt_atualizacao_estoque_w
	from	nota_fiscal
	where	nr_sequencia	= nr_seq_nota_p;

	if (ie_tipo_dado_p	= 9) then
		ds_retorno_w		:= dt_emissao_w;
	elsif (ie_tipo_dado_p	= 11) then
		ds_retorno_w		:= dt_emissao_w;
	elsif (ie_tipo_dado_p	= 13) then
		ds_retorno_w		:= dt_entrada_saida_w;
	elsif (ie_tipo_dado_p = 21) then
		select	min(a.dt_vencimento)
		into STRICT	ds_retorno_w
		from	nota_fiscal_venc a
		where	a.nr_sequencia	= nr_seq_nota_p;
	elsif (ie_tipo_dado_p = 27) then
		ds_retorno_w := dt_atualizacao_estoque_w;
	end if;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
