CREATE OR REPLACE FUNCTION obter_dados_nut_orient_classif (nr_seq_classif_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

/*
ie_opcao_p:
DS = Descricao
*/
if (ie_opcao_p = 'DS') then

	select	max(ds_classificacao)
	into STRICT	ds_retorno_w
	from	nut_orientacao_classif
	where	nr_sequencia = nr_seq_classif_p;

end if;

return	ds_retorno_w;

end;
$body$
