SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_nut_reserva_html5 ( nr_seq_nut_reserva_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w		timestamp;

dt_servico_w		timestamp;
ds_nut_servico_w	varchar(60);
nm_restaurante_w	varchar(80);
hr_inicio_w		timestamp;
hr_final_w		timestamp;



BEGIN

select	dt_servico,
	substr(obter_desc_nut_servico(nr_seq_servico),1,60),
	substr(obter_nome_restaurante(nr_seq_restaurante),1,80),
	hr_inicio,
	hr_final
into STRICT	dt_servico_w,
	ds_nut_servico_w,
	nm_restaurante_w,
	hr_inicio_w,
	hr_final_w
from	nut_reserva
where	nr_sequencia	= nr_seq_nut_reserva_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w := dt_servico_w;
elsif (ie_opcao_p = 'S') then
	ds_retorno_w := ds_nut_servico_w;
elsif (ie_opcao_p = 'R') then
	ds_retorno_w := nm_restaurante_w;
elsif (ie_opcao_p = 'I') then
	ds_retorno_w := hr_inicio_w;
else
	ds_retorno_w := hr_final_w;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
