SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_oc_gedipa ( nr_ordem_compra_p bigint, ie_opcao_p text, cd_material_p bigint, cd_unidade_p text) RETURNS varchar AS $body$
DECLARE


/* 'D' data da ordem de compra
     'U' usuario solicitante da ordem de compra 
      'E' status do item na ordem de compra */
				
ds_retorno_w	varchar(255);


BEGIN

	if (ie_opcao_p = 'D') then
		begin
		select to_char(b.dt_ordem_compra,'dd/mm/yyyy')
		into STRICT	ds_retorno_w
		from	ordem_compra b
		where   b.nr_ordem_compra = nr_ordem_compra_p;
	
		end;
	
	else if (ie_opcao_p = 'U') then
		begin
		select substr(obter_nome_pf(a.cd_pessoa_solicitante),1,254) nm_pessoa_solicitante
		into STRICT 	ds_retorno_w
		from    ordem_compra a
		where   a.nr_ordem_compra = nr_ordem_compra_p;
	
		end;
	
	else if (ie_opcao_p = 'E') then
		begin
		select substr(obter_status_item_transf(nr_ordem_compra_p,a.nr_item_oci),1,255) ds_status
		into STRICT	ds_retorno_w
		from   	ordem_compra_item a
		where   a.nr_ordem_compra 	=      nr_ordem_compra_p
		and	a.cd_material 		=      cd_material_p
		and	a.cd_unidade_medida_compra = cd_unidade_p
		order by nr_item_oci desc LIMIT 1;
		end;
		
	end if;
	end if;
	end if;
	

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
