CREATE OR REPLACE FUNCTION obter_dados_oftalmologia (nr_seq_consulta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_informacao_w		varchar(255);
ds_tipo_consulta_w	varchar(255);

BEGIN

if (nr_seq_consulta_p IS NOT NULL AND nr_seq_consulta_p::text <> '') then
	Select	MAX(b.ds_tipo_consulta)
	into STRICT	ds_tipo_consulta_w
	from 	oft_tipo_consulta b,
		oft_consulta a
	where 	a.nr_seq_tipo_consulta = b.nr_sequencia
	and 	a.nr_sequencia = nr_seq_consulta_p;

	if (ie_opcao_p = 'TC') then
		ds_informacao_w := ds_tipo_consulta_w;
	end if;
end if;

return	ds_informacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
