CREATE OR REPLACE FUNCTION obter_dados_operacao_estoque ( cd_operacao_estoque_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(40)	:= '';
ie_entrada_saida_w				varchar(01)	:= '';
ie_consignado_w				varchar(01)	:= '';
ie_tipo_requisicao_w			varchar(3)	:= '';

/* E - Entrada/Saída
   C - ie_consignado
   O - Tipo de operação.

*/
BEGIN

if (cd_operacao_estoque_p IS NOT NULL AND cd_operacao_estoque_p::text <> '') then

	select	ie_entrada_saida,
		ie_consignado,
		ie_tipo_requisicao
	into STRICT	ie_entrada_saida_w,
		ie_consignado_w,
		ie_tipo_requisicao_w
	from	operacao_estoque
	where	cd_operacao_estoque = cd_operacao_estoque_p;

	if (ie_tipo_p = 'E') then
		ds_retorno_w			:= ie_entrada_saida_w;
	elsif (ie_tipo_p = 'C') then
		ds_retorno_w			:= ie_consignado_w;
	elsif (ie_tipo_p = 'O') then
		ds_retorno_w			:= ie_tipo_requisicao_w;
	end if;

end if;
return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
