CREATE OR REPLACE FUNCTION obter_dados_opme_concat_fornec (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w varchar(4000);
ds_opme_w  varchar(255);
ds_qt_opme_w  varchar(20);
ds_fornec_w	varchar(255);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(774292, null, wheb_usuario_pck.get_nr_seq_idioma);--Mat 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(296944, null, wheb_usuario_pck.get_nr_seq_idioma);--QTDE 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(630398, null, wheb_usuario_pck.get_nr_seq_idioma);--Fornec. 
C01 CURSOR FOR 
	SELECT a.qt_material, 
		SUBSTR(OBTER_DESC_MATERIAL(a.cd_material),1,254), 
		substr(obter_dados_pf_pj(null,	cd_cgc, 'N'),1,254) 
	FROM  agenda_pac_opme a 
	WHERE  a.nr_seq_agenda  = nr_sequencia_p;


BEGIN 
 
ds_retorno_w := '';
ds_opme_w := '';
ds_qt_opme_w := '';
 
OPEN C01;
LOOP 
FETCH C01 INTO 
 ds_qt_opme_w, 
 ds_opme_w, 
 ds_fornec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
 BEGIN 
 ds_retorno_w := ds_retorno_w ||'  '||expressao1_w||': '|| ds_opme_w || '  '||upper(expressao2_w)||': '|| ds_qt_opme_w||'  '||expressao3_w||' '|| ds_fornec_w || chr(13);
 END;
END LOOP;
CLOSE C01;
 
RETURN ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
