SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_opm ( nr_seq_opm_p bigint, ie_opcao text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
ds_material_w		varchar(255);
cd_material_w		prescr_opm.cd_material%type;

BEGIN

if (coalesce(nr_seq_opm_p,0) > 0) then
	select 	max(substr(obter_desc_material(cd_material),1,255)),
   		max(cd_material)
	into STRICT	ds_material_w,
		cd_material_w
	from 	prescr_opm
	where 	nr_sequencia = nr_seq_opm_p;
end if;


if (ie_opcao = 'M') then
	ds_retorno_w:= ds_material_w;
elsif (ie_opcao = 'CM') then
	ds_retorno_w:= cd_material_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
