CREATE OR REPLACE FUNCTION obter_dados_op_opm ( nr_seq_op_p bigint, ie_opcao text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
ds_material_w		varchar(255);

BEGIN

if (coalesce(nr_seq_op_p,0) > 0) then
	select	SUBSTR(obter_dados_opm(NR_SEQ_OPM, 'M'),1,255)
	into STRICT	ds_material_w
	from	ordem_producao_opm
	where	nr_sequencia = nr_seq_op_p;
end if;

if (ie_opcao = 'M') then
	ds_retorno_w:= ds_material_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
