CREATE OR REPLACE FUNCTION obter_dados_op_requisicao ( nr_requisicao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
nr_op_w 		bigint;
/* 
ie_opcao_p 
 
OPF - Ordem de produção OPM + Pessoa física 
*/
 

BEGIN 
if (ie_opcao_p	= 'OPF') then	 
  select 	max(c.nr_seq_ordem_opm) 
	into STRICT		nr_op_w 
	from  	requisicao_material a, 
        item_requisicao_material b, 
        ordem_producao_comp_opm c 
	where  a.nr_requisicao = nr_requisicao_p 
  and   a.nr_requisicao = b.nr_requisicao 
  and   b.nr_seq_op_comp_opm = c.nr_sequencia;
 
  if (nr_op_w IS NOT NULL AND nr_op_w::text <> '') then 
   select  substr(nr_op_w || ' - ' || obter_nome_pf(max(a.cd_pessoa_fisica)),1,255) 
   into STRICT	 ds_retorno_w 
   from   prescr_medica a, 
         prescr_opm b 
   where  b.nr_ordem_opm = nr_op_w 
   and   b.nr_prescricao = a.nr_prescricao;
  end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_op_requisicao ( nr_requisicao_p bigint, ie_opcao_p text) FROM PUBLIC;

