CREATE OR REPLACE FUNCTION obter_dados_paciente_setor ( nr_seq_paciente_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(60);
cd_pessoa_fisica_w	varchar(10);
qt_altura_w		real;

/*
C - Codigo
D - Descricao
A - Altura
*/
BEGIN
if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then
	begin
	
	select	max(cd_pessoa_fisica),
          max(qt_altura)
	into STRICT	cd_pessoa_fisica_w,
        qt_altura_w
	from	paciente_setor 
	where	nr_seq_paciente	= nr_seq_paciente_p;
 
	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= cd_pessoa_fisica_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w	:= obter_nome_pessoa_fisica(cd_pessoa_fisica_w,null);
	elsif (ie_opcao_p = 'A') then
		ds_retorno_w 	:= qt_altura_w;
	end if;
	end;
end if;



return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
