SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_pac_prob_alta (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
'I' - dt_atualizacao_nrec
'A' - dt_previsao_alta
'P' - ie_probabilidade_alta
*/
dt_atualizacao_nrec_w		atend_paciente_prob_alta.dt_atualizacao_nrec%type;
dt_previsao_alta_w			atend_paciente_prob_alta.dt_previsao_alta%type;
ie_probabilidade_alta_w		atend_paciente_prob_alta.ie_probabilidade_alta%type;
nr_sequencia_w			atend_paciente_prob_alta.nr_sequencia%type;
ds_retorno_w			varchar(30) := '';


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	atend_paciente_prob_alta
where	nr_atendimento = nr_atendimento_p;

select	dt_atualizacao_nrec,
	dt_previsao_alta,
	ie_probabilidade_alta
into STRICT	dt_atualizacao_nrec_w,
	dt_previsao_alta_w,
	ie_probabilidade_alta_w
from	atend_paciente_prob_alta
where	nr_sequencia = nr_sequencia_w;

if (ie_opcao_p = 'I') and (dt_atualizacao_nrec_w IS NOT NULL AND dt_atualizacao_nrec_w::text <> '') then
	ds_retorno_w := to_char(dt_atualizacao_nrec_w,'dd/mm/yyyy hh24:mi:ss');
elsif (ie_opcao_p = 'A') and (dt_previsao_alta_w IS NOT NULL AND dt_previsao_alta_w::text <> '') then
	ds_retorno_w := to_char(dt_previsao_alta_w,'dd/mm/yyyy hh24:mi:ss');
elsif (ie_opcao_p = 'P') and (coalesce(ie_probabilidade_alta_w,'X') <> 'X') then
	ds_retorno_w := ie_probabilidade_alta_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
