SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_pac_setor_prescr ( nr_prescricao_p bigint, ie_opacao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_paciente_w	bigint;
ds_observacao_w		varchar(4000);
ds_retorno_w		varchar(4000);


BEGIN

if (nr_prescricao_p > 0) then

	select	coalesce(max(nr_seq_paciente),0)
	into STRICT	nr_seq_paciente_w
	from	paciente_atendimento
	where	nr_prescricao	=	nr_prescricao_p;

	if (nr_seq_paciente_w > 0) then
		select	max(ds_observacao)
		into STRICT	ds_observacao_w
		from	paciente_setor
		where	nr_seq_paciente	=	nr_seq_paciente_w;
	end if;

	if (ie_opacao_p = 'O') then
		ds_retorno_w	:= ds_observacao_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
