CREATE OR REPLACE FUNCTION obter_dados_padrao_mif (nr_sequencia_p bigint, ie_valor_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(400);


BEGIN

	select 	SUBSTR(CASE WHEN ie_valor_campo_p=7 THEN 'Independência completa' WHEN ie_valor_campo_p=6 THEN 'Independência modificada' WHEN ie_valor_campo_p=5 THEN 'Supervisão' WHEN ie_valor_campo_p=4 THEN 'Ajuda mínima' WHEN ie_valor_campo_p=3 THEN 'Ajuda moderada' WHEN ie_valor_campo_p=2 THEN 'Ajuda máxima' WHEN ie_valor_campo_p=1 THEN 'Ajuda total' END ,1,100)
	into STRICT	ds_retorno_w
	from	escala_mif
	where	nr_sequencia = nr_sequencia_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
