CREATE OR REPLACE FUNCTION obter_dados_pais (nr_seq_pais_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*
ie_opcao_p
 CS - CÃ³digo SIB
 N - Nome
 SG
*/
cd_pais_sib_w		varchar(30);
ds_retorno_w		varchar(255);
nm_pais_w		varchar(30);
sg_pais_w		varchar(03);


BEGIN

select	cd_pais_sib,
	nm_pais,
	sg_pais
into STRICT	cd_pais_sib_w,
	nm_pais_w,
	sg_pais_w
from	pais
where	nr_Sequencia = nr_seq_pais_p;

if (ie_opcao_p = 'CS') then
	ds_retorno_w	:= cd_pais_sib_w;
elsif (ie_opcao_p = 'N' ) then
	ds_retorno_w	:= nm_pais_w;
elsif (ie_opcao_p = 'SG' ) then
	ds_retorno_w	:= sg_pais_w;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
