CREATE OR REPLACE FUNCTION obter_dados_parametros_cih (ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := null;
nr_seq_parametro_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_parametro_w
from 	cih_parametros;

if (nr_seq_parametro_w IS NOT NULL AND nr_seq_parametro_w::text <> '') then

	if (ie_opcao_p = 'EV') then

		select	nr_seq_evento_infeccao
		into STRICT	ds_retorno_w
		from	cih_parametros
		where	nr_sequencia = nr_seq_parametro_w;

	elsif (ie_opcao_p = 'CS') then

		select	cd_caso_infeccao
		into STRICT	ds_retorno_w
		from	cih_parametros
		where	nr_sequencia = nr_seq_parametro_w;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
