CREATE OR REPLACE FUNCTION obter_dados_parametros_dcx ( nm_maquina_atual_p text, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(255);

/*
1 - Diretório de imagem
2 - Compressão de imagem
3 - Diretório de laudo
4 - Visualizador Carepoint
5 - IP conexão
6 - Porta conexão
*/
BEGIN


if (ie_opcao_p = 1) then --'D'
	select  max(a.ds_diretorio_imagens)
	into STRICT	ds_retorno_w
	from	parametro_integracao_dcx a,
		computador b
	where	a.nr_seq_computador = b.nr_sequencia
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and	b.nm_computador_pesquisa  = padronizar_nome(upper(nm_maquina_atual_p));

elsif (ie_opcao_p = 2) then --'C'
	select  to_char(somente_numero(max(a.QT_COMPRESSAO_IMAGEM)))
	into STRICT	ds_retorno_w
	from	parametro_integracao_dcx a,
		computador b
	where	a.nr_seq_computador = b.nr_sequencia
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and	b.nm_computador_pesquisa  = padronizar_nome(upper(nm_maquina_atual_p));

elsif (ie_opcao_p = 3) then --'L'
	select  max(a.ds_diretorio_laudo)
	into STRICT	ds_retorno_w
	from	parametro_integracao_dcx a,
		computador b
	where	a.nr_seq_computador = b.nr_sequencia
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and	b.nm_computador_pesquisa  = padronizar_nome(upper(nm_maquina_atual_p));

elsif (ie_opcao_p = 4) then --'V'
	select  max(a.ie_visualizador_cp)
	into STRICT	ds_retorno_w
	from	parametro_integracao_dcx a,
		computador b
	where	a.nr_seq_computador = b.nr_sequencia
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and	b.nm_computador_pesquisa  = padronizar_nome(upper(nm_maquina_atual_p));

elsif (ie_opcao_p = 5) then --'I'
	select 	max(a.ds_endereco_ip) ds_ip
	into STRICT	ds_retorno_w
	from 	parametro_integracao_dcx a,
		computador b
	where 	a.nr_seq_computador = b.nr_sequencia
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and	b.nm_computador_pesquisa  = padronizar_nome(upper(nm_maquina_atual_p));

elsif (ie_opcao_p = 6) then --'P'
	select 	max(a.ds_endereco_porta) ds_porta
	into STRICT	ds_retorno_w
	from 	parametro_integracao_dcx a,
		computador b
	where 	a.nr_seq_computador = b.nr_sequencia
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and	b.nm_computador_pesquisa  = padronizar_nome(upper(nm_maquina_atual_p));

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
