CREATE OR REPLACE FUNCTION obter_dados_param_estoque (cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(255);
ie_mes_atual_regra_fornec_w			varchar(1);
ie_local_estoque_w				varchar(1);
ie_bloqueio_lote_w			varchar(1);
qt_dia_ajuste_maximo_w			integer;
qt_dia_ajuste_minimo_w			integer;
ie_saldo_requisicao_w			varchar(1);

/* SMA - Somente mês atual */

/* E - Local estoque */

/*QDMAX - Quantidade de dias para ajuste estoque máximo*/

/*QDMIN - Quantidade de dias para ajuste estoque mínimo*/

/*BL - Bloqueio lote*/

/*SR - Saldo requisição*/

BEGIN
select	coalesce(max(ie_mes_atual_regra_fornec),'N'),
	coalesce(max(ie_local_estoque), 'S'),
	coalesce(max(qt_dia_ajuste_maximo),0),
	coalesce(max(qt_dia_ajuste_minimo),0),
	coalesce(max(ie_bloqueio_lote),0),
	coalesce(max(ie_saldo_requisicao),'C')
into STRICT	ie_mes_atual_regra_fornec_w,
	ie_local_estoque_w,
	qt_dia_ajuste_maximo_w,
	qt_dia_ajuste_minimo_w,
	ie_bloqueio_lote_w,
	ie_saldo_requisicao_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_opcao_p = 'SMA') then
	ds_retorno_w	:= ie_mes_atual_regra_fornec_w;
elsif (ie_opcao_p = 'E') then
	ds_retorno_w	:= ie_local_estoque_w;
elsif (ie_opcao_p = 'QDMAX') then
	ds_retorno_w	:= to_char(qt_dia_ajuste_maximo_w);
elsif (ie_opcao_p = 'QDMIN') then
	ds_retorno_w	:= to_char(qt_dia_ajuste_minimo_w);
elsif (ie_opcao_p = 'BL') then
	ds_retorno_w	:= ie_bloqueio_lote_w;
elsif (ie_opcao_p = 'SR') then
	ds_retorno_w	:= ie_saldo_requisicao_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
