CREATE OR REPLACE FUNCTION obter_dados_param_int_compras ( cd_estabelecimento_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


nm_projeto_w		varchar(80);
nm_empresa_w		varchar(80);
nm_org_compra_w		varchar(80);
nm_centro_w		varchar(80);
ds_local_entrega_w		varchar(80);

ds_retorno_w		varchar(80);



BEGIN

select	coalesce(max(nm_projeto_borg), 'HSL'),
	coalesce(max(nm_empresa_borg), 'HSL'),
	coalesce(max(nm_org_compra_borg), 'HSL'),
	coalesce(max(nm_centro_borg), 'HSL'),
	coalesce(max(ds_local_entrega), 'HSL01')
into STRICT	nm_projeto_w,
	nm_empresa_w,
	nm_org_compra_w,
	nm_centro_w,
	ds_local_entrega_w
from	parametro_int_compras
where	cd_estabelecimento	= cd_estabelecimento_p;



if (ie_tipo_p = 'P') then
	ds_retorno_w := nm_projeto_w;
elsif (ie_tipo_p = 'E') then
	ds_retorno_w := nm_empresa_w;
elsif (ie_tipo_p = 'O') then
	ds_retorno_w := nm_org_compra_w;
elsif (ie_tipo_p = 'C') then
	ds_retorno_w := nm_centro_w;
elsif (ie_tipo_p = 'LE') then
	ds_retorno_w := ds_local_entrega_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
