SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_parcela_neg_cr ( nr_seq_parcela_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
nr_retorno_w		double precision;
nr_seq_negociacao_w	bigint;
dt_vencimento_w		timestamp;
	
/* 
 IE_OPCAO _P 
'TI' = Título 
'VS' = Valor saldo 
'SI' = Situação 
*/
 
	 

BEGIN 
 
if (nr_seq_parcela_p IS NOT NULL AND nr_seq_parcela_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then 
	select	a.nr_seq_negociacao, 
		a.dt_vencimento 
	into STRICT	nr_seq_negociacao_w, 
		dt_vencimento_w 
	from	negociacao_cr_parcela a 
	where	a.nr_sequencia	= nr_seq_parcela_p;
	 
	if (ie_opcao_p = 'TI') then 
		select	max(a.nr_titulo) 
		into STRICT	nr_retorno_w 
		from	negociacao_cr_boleto a, 
				negociacao_cr_parcela b 
		where	a.nr_seq_negociacao						= b.nr_seq_negociacao 
		and		a.nr_seq_negociacao						 = nr_seq_negociacao_w 
		and		b.nr_sequencia 							= nr_seq_parcela_p 
		and		to_date(a.dt_vencimento,'dd/mm/RRRR hh24:mi:ss') 	= to_date(b.dt_vencimento,'dd/mm/RRRR hh24:mi:ss') 
		and		to_date(a.dt_vencimento,'dd/mm/RRRR hh24:mi:ss') 	= to_date(dt_vencimento_w,'dd/mm/RRRR hh24:mi:ss');
	 
	end if;
		 
	if (ie_opcao_p = 'VS') then	 
		select	max(obter_dados_titulo_receber(a.nr_titulo, 'VS')) 
		into STRICT	nr_retorno_w 
		from	negociacao_cr_boleto a, 
				negociacao_cr_parcela b 
		where	a.nr_seq_negociacao 					= b.nr_seq_negociacao 
		and		a.nr_seq_negociacao						= nr_seq_negociacao_w 
		and		b.nr_sequencia 							= nr_seq_parcela_p 
		and		to_date(a.dt_vencimento,'dd/mm/RRRR hh24:mi:ss')	= to_date(b.dt_vencimento,'dd/mm/RRRR hh24:mi:ss') 
		and		to_date(a.dt_vencimento,'dd/mm/RRRR hh24:mi:ss') 	= to_date(dt_vencimento_w,'dd/mm/RRRR hh24:mi:ss');
	end if;
		 
	if (ie_opcao_p = 'SI') then 
		select	max(obter_dados_titulo_receber(a.nr_titulo, 'SI')) 
		into STRICT	nr_retorno_w 
		from	negociacao_cr_boleto a, 
				negociacao_cr_parcela b 
		where	a.nr_seq_negociacao 					= b.nr_seq_negociacao 
		and		a.nr_seq_negociacao 					= nr_seq_negociacao_w 
		and		b.nr_sequencia 							= nr_seq_parcela_p 
		and		to_date(a.dt_vencimento,'dd/mm/RRRR hh24:mi:ss') 	= to_date(b.dt_vencimento,'dd/mm/RRRR hh24:mi:ss') 
		and		to_date(a.dt_vencimento,'dd/mm/RRRR hh24:mi:ss') 	= to_date(dt_vencimento_w,'dd/mm/RRRR hh24:mi:ss');
	end if;
end if;	
	 
return	nr_retorno_w;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
