SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_parecer_medico ( nr_parecer_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
- 'SC'	= CÃ³digo do status do contato
- 'DC'	= Data do contato
*/
nr_seq_contato_w		bigint;
ds_retorno_w		varchar(255) := '';
ie_lib_parecer_medico_w	varchar(255);


BEGIN

if (nr_parecer_p IS NOT NULL AND nr_parecer_p::text <> '') then

Select 	max(ie_lib_parecer_medico)
into STRICT	ie_lib_parecer_medico_w
from	parametro_medico
where 	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;



	begin

	if (ie_lib_parecer_medico_w = 'S') then

		select	max(nr_sequencia)
		into STRICT	nr_seq_contato_w
		from	parecer_medico_contato
		where	nr_parecer = nr_parecer_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	coalesce(dt_inativacao::text, '') = '';

	else

		select	max(nr_sequencia)
		into STRICT	nr_seq_contato_w
		from	parecer_medico_contato
		where	nr_parecer = nr_parecer_p;


	end if;

	if (ie_opcao_p = 'SC') then
		begin
		if (nr_seq_contato_w IS NOT NULL AND nr_seq_contato_w::text <> '') then
			select	max(ie_status_contato)
			into STRICT	ds_retorno_w
			from	parecer_medico_contato
			where	nr_sequencia = nr_seq_contato_w;
		end if;
		end;
	elsif (ie_opcao_p = 'DC') then
		begin
		if (nr_seq_contato_w IS NOT NULL AND nr_seq_contato_w::text <> '') then
			select	max(to_char(dt_contato,'dd/mm/yyyy hh24:mi:ss'))
			into STRICT	ds_retorno_w
			from	parecer_medico_contato
			where	nr_sequencia = nr_seq_contato_w;
		end if;
		end;
	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
