CREATE OR REPLACE FUNCTION obter_dados_parecer (nr_parecer_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(255);
				

BEGIN
if (ie_opcao_p = 'RS') then
	
	select	CASE WHEN max(IE_STATUS)='A' THEN OBTER_DESC_EXPRESSAO(283171) WHEN max(IE_STATUS)='F' THEN  OBTER_DESC_EXPRESSAO(290157)  ELSE OBTER_DESC_EXPRESSAO(331353) END
	into STRICT	ie_retorno_w
	from	parecer_medico
	where	nr_parecer = nr_parecer_p
	and   	coalesce(dt_inativacao::text, '') = ''
	and   	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

elsif (ie_opcao_p = 'RD') then
	
	select	max(to_char(DT_LIBERACAO,'dd/mm/yyyy hh24:mi:ss'))
	into STRICT	ie_retorno_w
	from	parecer_medico
	where	nr_parecer = nr_parecer_p
	and   	coalesce(dt_inativacao::text, '') = ''
	and   	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	
elsif (ie_opcao_p = 'RP') then
	
	select	max(obter_nome_pf(cd_medico))
	into STRICT	ie_retorno_w
	from	parecer_medico
	where	nr_parecer = nr_parecer_p
	and   	coalesce(dt_inativacao::text, '') = ''
	and   	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
