CREATE OR REPLACE FUNCTION obter_dados_parto ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
'1' - Nascidos Vivos
'2' - Nascidos Mortos
*/
qt_nasc_vivos_w		smallint;
qt_nasc_mortos_w	smallint;



BEGIN

select	sum(qt_nasc_vivos),
	sum(qt_nasc_mortos)
into STRICT	qt_nasc_vivos_w,
	qt_nasc_mortos_w
from 	parto
where	nr_atendimento = nr_atendimento_p;

if (coalesce(ie_opcao_p,'1') = '1') then
	return	qt_nasc_vivos_w;
elsif (coalesce(ie_opcao_p,'1') = '2') then
	return	qt_nasc_mortos_w;
end if;

end;
