CREATE OR REPLACE FUNCTION obter_dados_pcr ( nr_seq_pcr_p bigint, ie_opcao_p text ) RETURNS bigint AS $body$
DECLARE


/*
Quantidade de profissionais	'P'
*/
qt_profissionais_w	bigint := 0;


BEGIN

if (ie_opcao_p = 'P') then

	Select	count(*)
	into STRICT	qt_profissionais_w
	from	qua_evento_pcr_prof
	where	nr_seq_pcr = nr_seq_pcr_p;

end if;

if (qt_profissionais_w > 0) then

	return	qt_profissionais_w;

else

	return	1;

end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
