CREATE OR REPLACE FUNCTION obter_dados_pf_aux ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, campo_p text) RETURNS varchar AS $body$
DECLARE

wreturn varchar(255) := null;


BEGIN
	
    if (campo_p = 'C') then
	select 	max(a.nr_digito_verif)
	into STRICT 	wreturn 
	from 	pessoa_fisica_aux a 
	where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
	
    elsif ( campo_p = 'SD' ) then
	select 	max(a.sg_departamento)
	into STRICT 	wreturn 
	from 	pessoa_fisica_aux a 
	where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
	
    elsif ( campo_p = 'DS' ) then
	select 	max(a.ds_digito_verif)
	into STRICT 	wreturn 
	from 	pessoa_fisica_aux a 
	where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
    end if;

return wreturn;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
