SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_pf_cns (cd_cns_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPCAO_P
CD	- Cd pessoa fisica
*/
ds_retorno_w		varchar(255);
cd_pessoa_fisica_w	varchar(10);


BEGIN

if (coalesce(cd_cns_p,'0') <> '0') then
	begin

	begin
	select	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	pessoa_fisica
	where	nr_cartao_nac_sus = cd_cns_p  LIMIT 1;
	exception
	when others then
		cd_pessoa_fisica_w := '0';
	end;

	if (cd_pessoa_fisica_w <> '0') then
		begin
		if (ie_opcao_p = 'CD') then
			ds_retorno_w := cd_pessoa_fisica_w;
		end if;
		end;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
