SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_pf_dt ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


/* IE_OPCAO_P
DN	  - Data de nascimento
DO	  - Data de óbito
DRG	  - Data de emissão da carteira de identidade
VRG	  - Data de validade da carteira de identidade
DCB	  - Data chegada Brasil
ECTPS - Data de emissão do CTPS
DEC	  - Data de emissão da certidão de casamento
DNAT  - Data da naturalização
*/
ds_retorno_w timestamp;


BEGIN
if (coalesce(cd_pessoa_fisica_p,'0') <> '0') then
	begin

	begin
	if (ie_opcao_p	= 'DN') then
		select	max(dt_nascimento)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p	= 'DO') then
		select	max(dt_obito)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p	= 'DRG') then
		select	max(dt_emissao_ci)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p	= 'VRG') then
		select	max(dt_validade_rg)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p	=  'DCB') then
		select	max(dt_chegada_brasil)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p	=  'ECTPS') then
		select	max(dt_emissao_ctps)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p	=  'DEC') then
		select	max(dt_emissao_cert_casamento)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p	=  'DNAT') then
		select	max(dt_naturalizacao_pf)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	end if;
	end;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
