SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_pf_inter (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w   varchar(255);

BEGIN

if (ie_opcao_p = 'RFC') then

	select 'XEXX010101000'
	into STRICT    ds_retorno_w
	from    nota_fiscal a,
		pessoa_fisica b,
		pessoa_fisica_conta c
	where   a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and     b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and     a.nr_sequencia = nr_sequencia_p
	and     obter_se_brasileiro(b.cd_pessoa_fisica) = 'N';

	ds_retorno_w := 'XEXX010101000';

elsif (ie_opcao_p = 'B') then

	select  max(obter_nome_banco(c.cd_banco))
	into STRICT	ds_retorno_w
	from    nota_fiscal a,
		pessoa_fisica b,
		pessoa_fisica_conta c
	where   a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and     b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and     a.nr_sequencia = nr_sequencia_p
	and     obter_se_brasileiro(b.cd_pessoa_fisica) = 'N';

elsif (ie_opcao_p = 'C') then

	select	max(c.nr_conta)
	into STRICT	ds_retorno_w
	from    nota_fiscal a,
		pessoa_fisica b,
		pessoa_fisica_conta c
	where   a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and     b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and     a.nr_sequencia = nr_sequencia_p
	and     obter_se_brasileiro(b.cd_pessoa_fisica) = 'N';

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
