SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_pg_inf_adic_atend ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_informacao1_w		varchar(255);
ds_informacao2_w		varchar(255);
ds_retorno_w			varchar(255);

c01 CURSOR FOR
SELECT 	substr(obter_dados_pg_inf_adic(nr_seq_inf_adic, 'D'),1,255),
		substr(obter_dados_pg_inf_adic_item(nr_seq_inf_adic_item, 'D'),1,255)
from	atend_perda_ganho_inf_adic a
where	nr_seq_atend_pg = nr_sequencia_p
order by 1,2;


BEGIN

if (nr_sequencia_p > 0) then
	if (ie_opcao_p = 'D') then
		open C01;
		loop
			fetch C01 into	ds_informacao1_w,
							ds_informacao2_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
			if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
				ds_retorno_w	:=	ds_retorno_w || ', ';
			end if;
			ds_retorno_w	:=	ds_retorno_w || ds_informacao1_w || ': ' || ds_informacao2_w;
		end loop;
		close C01;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
