CREATE OR REPLACE FUNCTION obter_dados_pg_inf_adic ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_informacao_w		varchar(255);
ds_retorno_w		varchar(255);


BEGIN

if (nr_sequencia_p > 0) then
	select	ds_informacao
	into STRICT	ds_informacao_w
	from	perda_ganho_inf_adic
	where	nr_sequencia = nr_sequencia_p;
end if;

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_informacao_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
