SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_pj_inf_trib (ds_tipo_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


retorno_w			varchar(256);
qt_registros_w		bigint;
nr_seq_tipo_documento_w	bigint;


BEGIN

if (ds_tipo_p = 'DOC') then

	select	count(*)
	into STRICT	qt_registros_w
	from	pessoa_juridica_inf_trib
	where	cd_cgc = cd_cgc_p;

	if (qt_registros_w > 0) then

		select	coalesce(nr_seq_tipo_documento,0)
		into STRICT	nr_seq_tipo_documento_w
		from	pessoa_juridica_inf_trib
		where	cd_cgc = cd_cgc_p;

		if (nr_seq_tipo_documento_w <> 0) then

			select	cd_tipo_documento
			into STRICT	retorno_w
			from	nfse_tipo_doc_fiscal
			where	nr_sequencia = nr_seq_tipo_documento_w;

		end if;


	end if;

end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
