CREATE OR REPLACE FUNCTION obter_dados_plano_versao_ans ( ie_tipo_dado_p text, nr_seq_conta_ans_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (ie_tipo_dado_p = 'CL') then /* retorna a classificação da conta do plano ans*/
	select	max(cd_classificacao)
	into STRICT	ds_retorno_w
	from	conta_contabil_ans a,
		ctb_plano_ans b
	where	a.nr_seq_conta_ans	= b.nr_sequencia
	and  	a.nr_seq_conta_ans  	= nr_seq_conta_ans_p;

elsif (ie_tipo_dado_p = 'DS') then /* retorna a descrição da conta do plano ans */
	select	max(c.ds_conta)
	into STRICT	ds_retorno_w
	from	conta_contabil_ans a,
		ctb_plano_ans c
	where 	a.nr_seq_conta_ans	= c.nr_sequencia
	and  	a.nr_seq_conta_ans  	= nr_seq_conta_ans_p;

elsif (ie_tipo_dado_p = 'VE') then /* retorna a descrição da versão do plano ans */
	select	max(b.ds_versao_ans)
	into STRICT	ds_retorno_w
	from	conta_contabil_ans a,
		ctb_versao_plano_ans b,
		ctb_plano_ans c
	where 	a.nr_seq_conta_ans	= c.nr_sequencia
	and 	c.nr_seq_versao_plano 	= b.nr_sequencia
	and  	a.nr_seq_conta_ans  	= nr_seq_conta_ans_p;

elsif (ie_tipo_dado_p = 'CD') then

	select	max(b.cd_plano)
	into STRICT	ds_retorno_w
	from	conta_contabil_ans a,
		ctb_plano_ans b
	where	a.nr_seq_conta_ans	= b.nr_sequencia
	and  	a.nr_seq_conta_ans  	= nr_seq_conta_ans_p;
end if;
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_plano_versao_ans ( ie_tipo_dado_p text, nr_seq_conta_ans_p bigint) FROM PUBLIC;

