CREATE OR REPLACE FUNCTION obter_dados_ponto_integr (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';
nr_seq_int_xml_w	bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(nr_seq_int_xml)
	into STRICT	nr_seq_int_xml_w
	from	int_ponto_integr
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'XML') then
		ds_retorno_w := nr_seq_int_xml_w;
	end if;


end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
