CREATE OR REPLACE FUNCTION obter_dados_pps ( ie_opcao_p text, dt_inicio_p timestamp, dt_final_p timestamp, ds_filtro_p text default null) RETURNS bigint AS $body$
DECLARE

/* 
Total de avaliaçãoes	'TA' 
 
*/
			 
qt_retorno_w	bigint;
	

BEGIN 
 
If (ie_opcao_p = 'TA') then 
	 
	if (coalesce(dt_final_p::text, '') = '') then 
		select 	count(*) 
		into STRICT	qt_retorno_w	 
		from	escala_pps a, 
			atendimento_paciente b 
		where	a.nr_atendimento = b.nr_atendimento 
		and	coalesce(a.dt_inativacao::text, '') = '' 
		and	trunc(a.dt_avaliacao) = trunc(dt_inicio_p);
	else 
		select 	count(*) 
		into STRICT	qt_retorno_w	 
		from	escala_pps a, 
			atendimento_paciente b 
		where	a.nr_atendimento = b.nr_atendimento 
		and	coalesce(a.dt_inativacao::text, '') = '' 
		and	trunc(a.dt_avaliacao) between trunc(dt_inicio_p) and trunc(dt_final_p);
	end if;
	 
end if;
return	qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
