CREATE OR REPLACE FUNCTION obter_dados_preco_pj ( cd_cnpj_p text, cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
U - Unidade medida do fornecedor (CD_UNIDADE_FORNEC)
C - Código do material do fornecedor( CD_ITEM)
D - Descrição do item fornecedor (DS_ITEM)
B - Código de barras (CD_BARRA)*/
nr_sequencia_w			bigint;
cd_unidade_fornec_w		varchar(20);
cd_item_w			varchar(20);
ds_item_w			varchar(100);
cd_barra_w			varchar(20);
ds_retorno_w			varchar(100);


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	preco_pj
where	cd_cgc = cd_cnpj_p
and	cd_material = cd_material_p
and	(((trunc(dt_vigencia,'dd') IS NOT NULL AND (trunc(dt_vigencia,'dd'))::text <> '')) and
	(trunc(dt_vigencia,'dd') = (	SELECT	max(dt_vigencia)
				from	preco_pj
				where	cd_cgc = cd_cnpj_p
				and	cd_material = cd_material_p
				and	trunc(dt_vigencia,'dd') <= trunc(clock_timestamp(),'dd')
				and	trunc(dt_vigencia_fim,'dd') >= trunc(clock_timestamp(),'dd'))) or
	not exists (	select	1
		from	preco_pj
		where	cd_cgc = cd_cnpj_p
		and	cd_material = cd_material_p
		and	trunc(dt_vigencia,'dd') <= trunc(clock_timestamp(),'dd')
		and	trunc(dt_vigencia_fim,'dd') >= trunc(clock_timestamp(),'dd')));

if (nr_sequencia_w > 0) then

	select	cd_unidade_fornec,
		cd_item,
		ds_item,
		cd_barra
	into STRICT	cd_unidade_fornec_w,
		cd_item_w,
		ds_item_w,
		cd_barra_w
	from	preco_pj
	where	nr_sequencia = nr_Sequencia_w;

	if (ie_opcao_p = 'U') then
		ds_retorno_w := cd_unidade_fornec_w;
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w := cd_item_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w := ds_item_w;
	elsif (ie_opcao_p = 'B') then
		ds_retorno_w := cd_barra_w;
	end if;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
