CREATE OR REPLACE FUNCTION obter_dados_prescricao_hemo (nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_irradiado_w		varchar(1);
ie_lavado_w		varchar(1);
ie_filtrado_w		varchar(1);
ie_aliquotado_w		varchar(1);
ie_retorno_w		varchar(1);

	/*
	I - Irradiado
	F - Filtrado
	A - Aliquotado
	L - Lavado
	*/
BEGIN

select	coalesce(max(ie_irradiado),'N'),
	coalesce(max(ie_lavado),'N'),
	coalesce(max(ie_filtrado),'N'),
	coalesce(max(ie_aliquotado),'N')
into STRICT	ie_irradiado_w,
	ie_lavado_w,
	ie_filtrado_w,
	ie_aliquotado_w
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia	= nr_seq_prescr_p;

if (ie_opcao_p = 'I') then
	ie_retorno_w	:= ie_irradiado_w;
elsif (ie_opcao_p = 'L') then
	ie_retorno_w	:= ie_lavado_w;
elsif (ie_opcao_p = 'F') then
	ie_retorno_w	:= ie_filtrado_w;
elsif (ie_opcao_p = 'A') then
	ie_retorno_w	:= ie_aliquotado_w;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
