CREATE OR REPLACE FUNCTION obter_dados_prescr_agenda ( nr_seq_agenda_p bigint, ie_opcao_p text, nr_prescricao_p bigint default 0) RETURNS varchar AS $body$
DECLARE


/* P - Para retornar se prescrição gerada */

/* IC - Para retornar se todos os materiais foram consistidos por barra */

/* IB - Para retornar se todos os materiais foram baixados*/

nr_prescricao_w		bigint;
qt_material_w		bigint;
qt_material_consist_w	bigint;
qt_material_baixados_w	bigint;
qt_material_prescr_w	bigint;
nr_cirurgia_w		bigint;


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	select	max(nr_cirurgia)
	into STRICT	nr_cirurgia_w
	from	agenda_paciente
	where	nr_sequencia	=	nr_seq_agenda_p;

	select	coalesce(max(nr_prescricao),0)
	into STRICT	nr_prescricao_w
	from	cirurgia
	where	nr_cirurgia	=	nr_cirurgia_w;

	if (nr_prescricao_w = 0) then
		select	max(nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica
		where	nr_seq_agenda	=	nr_seq_agenda_p
		and	coalesce(ie_tipo_prescr_cirur::text, '') = '';
	end if;
else
	nr_prescricao_w := nr_prescricao_p;
end if;

if	(ie_opcao_p = 'IC' AND nr_prescricao_w > 0) then
	select	count(*)
	into STRICT	qt_material_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_w;

	select	count(*)
	into STRICT	qt_material_consist_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_w
	and	ie_status_cirurgia	in ('CB','AD');
end if;

if	(ie_opcao_p = 'IB' AND nr_prescricao_w > 0) then
	select	count(*)
	into STRICT	qt_material_prescr_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_w
	and	qt_material 		> 0;

	select	count(*)
	into STRICT	qt_material_baixados_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_w
	and	cd_motivo_baixa		<> 0
	and	qt_material 		> 0;
end if;


if (ie_opcao_p = 'P') and (nr_prescricao_w > 0) then
	return 'S';
elsif (ie_opcao_p = 'IC') then
	if (qt_material_w = qt_material_consist_w) and (qt_material_w > 0) then
		return	'S';
	else
		return	'N';
	end if;
elsif (ie_opcao_p = 'IB') then
	if (qt_material_prescr_w = qt_material_baixados_w) and (qt_material_prescr_w > 0) then
		return	'S';
	elsif (qt_material_prescr_w > qt_material_baixados_w) and (qt_material_baixados_w > 0) then
		return	'B';
	else
		return 'N';
	end if;
else
	return	'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_prescr_agenda ( nr_seq_agenda_p bigint, ie_opcao_p text, nr_prescricao_p bigint default 0) FROM PUBLIC;

