SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_prescr_consist ( nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* P - Para retornar se prescrição gerada */

/* IC - Para retornar se todos os materiais foram consistidos por barra */

nr_prescricao_w		bigint;
qt_material_w		smallint;
qt_material_consist_w	smallint;


BEGIN

nr_prescricao_w	:= coalesce(nr_prescricao_p,0);

if	(ie_opcao_p = 'IC' AND nr_prescricao_w > 0) then
	select	count(*)
	into STRICT	qt_material_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_w;

	select	count(*)
	into STRICT	qt_material_consist_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_w
	and	ie_status_cirurgia	in ('CB','AD');
end if;

if (ie_opcao_p = 'P') and (nr_prescricao_w > 0) then
	return 'S';
elsif (ie_opcao_p = 'IC') then
	if (qt_material_w = qt_material_consist_w) and (qt_material_w > 0) then
		return	'S';
	else
		return	'N';
	end if;
else
	return	'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_prescr_consist ( nr_prescricao_p bigint, ie_opcao_p text) FROM PUBLIC;

