SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_prescr_enf (nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
D - Data prescrição 
P - Prescritor 
*/
 
 
ds_retorno_w		varchar(255);
dt_prescricao_w		timestamp;
dt_inicio_prescr_w	timestamp;
dt_validade_prescr_w	timestamp;
cd_prescritor_w		varchar(10);


BEGIN 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	select	dt_prescricao, 
		cd_prescritor, 
		dt_inicio_prescr, 
		dt_validade_prescr 
	into STRICT	dt_prescricao_w, 
		cd_prescritor_w, 
		dt_inicio_prescr_w, 
		dt_validade_prescr_w 
	from	pe_prescricao 
	where	nr_sequencia = nr_prescricao_p;
 
	if (coalesce(ie_opcao_p,'D') = 'D') then 
		ds_retorno_w := to_char(dt_prescricao_w,'dd/mm/yyyy hh24:mi:ss');
	elsif (ie_opcao_p = 'P') then 
		ds_retorno_w := cd_prescritor_w;
	elsif (ie_opcao_p = 'I') then	 
		ds_retorno_w := to_char(dt_inicio_prescr_w,'dd/mm/yyyy hh24:mi:ss');
	elsif (ie_opcao_p = 'T') then		 
		ds_retorno_w := to_char(dt_validade_prescr_w,'dd/mm/yyyy hh24:mi:ss');
	end if;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
