CREATE OR REPLACE FUNCTION obter_dados_prescr_exame_js (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


nm_exame_w	varchar(30);
dt_aprovacao_w  timestamp;
dt_coleta_w	timestamp;
dt_digitacao_w	timestamp;
ds_retorno_w	varchar(4000);
ds_retorno1_w	varchar(500);

c01 CURSOR FOR
SELECT	substr(obter_desc_exame(b.nr_seq_exame),1,30),
	dt_aprovacao,
	coalesce(to_date(lab_obter_data_recoleta(nr_prescricao_p,b.NR_SEQ_PRESCR,a.nm_usuario,0),'dd/mm/yyyy hh24:mi:ss') ,(b.dt_coleta)),
	b.dt_digitacao
from	exame_lab_result_item b,
	exame_lab_resultado a
where	a.nr_prescricao = nr_prescricao_p
and	a.nr_seq_resultado = b.nr_seq_resultado
and	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');


BEGIN
	open c01;
	loop
	fetch c01 into
		nm_exame_w,
		dt_aprovacao_w,
		dt_coleta_w,
		dt_digitacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		ds_retorno_w	:= ds_retorno_w || rpad(nm_exame_w,32,' ') || rpad(coalesce(to_char(dt_coleta_w,'dd/mm/yyyy hh24:mi:ss'),'Sem data'),28,' ') || rpad(coalesce(to_char(dt_digitacao_w,'dd/mm/yyyy hh24:mi:ss'),'Sem data'),40,' ') || rpad(to_char(dt_aprovacao_w,'dd/mm/yyyy hh24:mi:ss'),25,' ') ||  chr(10) || chr(13);

		end;

	end loop;
	close C01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno1_w	:= rpad('Exame',36,' ') || rpad('Data de coleta',35,' ') || rpad('Data de digitação',35,' ') || rpad('Data de aprovação',25,' ');
	ds_retorno_w	:= ds_retorno1_w ||  chr(10) || chr(13) || ds_retorno_w;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
