CREATE OR REPLACE FUNCTION obter_dados_prescr_proc_pragma_atx ( nr_prescricao_p bigint, nr_sequencia_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p:
C - Cobra paciente
L - Lado
*/
ds_retorno_w		varchar(255)	:= '';
ie_cobra_paciente_w	prescr_procedimento.ie_cobra_paciente%type;
ie_lado_w		prescr_procedimento.ie_lado%type;
BEGIN

select	max(ie_cobra_paciente),
	max(ie_lado)
into STRICT	ie_cobra_paciente_w,
	ie_lado_w
from	prescr_procedimento
where	nr_prescricao		= nr_prescricao_p
and	nr_sequencia		= nr_sequencia_prescricao_p;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= ie_cobra_paciente_w;
elsif (ie_opcao_p = 'L') then
	ds_retorno_w	:= ie_lado_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
