SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_prescr_proc (nr_prescricao_p bigint, nr_seq_procedimento_p bigint, ie_opcao_p text, ie_exibe_dose_glicemia_p text) RETURNS varchar AS $body$
DECLARE


/*
opções
QIL - quantidade x intervalo x lado
IC - Indicação Clínica
*/
ds_retorno_w		varchar(2000);
qt_procedimento_w	double precision;
cd_intervalo_w	varchar(7);
ie_lado_w		varchar(15);
ds_dado_clinico_w	varchar(2000);
ie_busca_ind_clinic_w   varchar(1);


BEGIN

ie_busca_ind_clinic_w := Obter_Param_Usuario(28, 296, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_busca_ind_clinic_w);

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') then
	select	coalesce(qt_procedimento,0),
		cd_intervalo,
		CASE WHEN ie_lado='E' THEN 'Esq' WHEN ie_lado='D' THEN 'Dir' WHEN ie_lado='A' THEN obter_desc_expressao(308326)/*'Ambos'*/  ELSE null END ,
		ds_dado_clinico
	into STRICT	qt_procedimento_w,
		cd_intervalo_w,
		ie_lado_w,
		ds_dado_clinico_w
	from	prescr_procedimento
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_procedimento_p;

	if (coalesce(ie_opcao_p,'QIL') = 'QIL') then

		if (qt_procedimento_w IS NOT NULL AND qt_procedimento_w::text <> '') and (coalesce(ie_exibe_dose_glicemia_p,'S') = 'S')	then
			ds_retorno_w	:= qt_procedimento_w;
		end if;
		if (cd_intervalo_w IS NOT NULL AND cd_intervalo_w::text <> '') then
			ds_retorno_w	:= ds_retorno_w || '  ' || substr(obter_desc_intervalo_prescr(cd_intervalo_w),1,40);
		end if;
		if (ie_lado_w IS NOT NULL AND ie_lado_w::text <> '') then
			ds_retorno_w	:= ds_retorno_w || '  ' || ie_lado_w;
		end if;
	elsif (coalesce(ie_opcao_p,'IC') = 'IC') then

	     if (ie_busca_ind_clinic_w = 'S') and (wheb_usuario_pck.get_cd_funcao <> 3004) then
	        select  max(DS_INDICACAO)
                into STRICT    ds_retorno_w
                from    procedimento_paciente
                where   nr_prescricao = nr_prescricao_p
                and     nr_sequencia_prescricao = nr_seq_procedimento_p;

	     else
		ds_retorno_w	:= ds_dado_clinico_w;
	     end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
