CREATE OR REPLACE FUNCTION obter_dados_prescr_solucao ( nr_sequencia_solucao_p bigint, nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_sequencia_solucao_p IS NOT NULL AND nr_sequencia_solucao_p::text <> '') ( (nr_prescricao_p) IS NOT NULL AND (nr_prescricao_p)::text <> '') then
	if (ie_opcao_p = 'D') then
		select	max(substr(obter_desc_expressao(288033) ||obter_valor_dominio(1537,ie_bomba_infusao),1,254))
		into STRICT	ds_retorno_w
		from 	prescr_solucao
		where 	nr_seq_solucao = nr_sequencia_solucao_p
		and 	nr_prescricao = nr_prescricao_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
